import {
    SCENARIO_LIST,
    SCENARIO_LIST_NO_DATA,
  } from '../constants/stateConstants';
  
  export const fromModel = action => ({
    [SCENARIO_LIST]: action.payload[SCENARIO_LIST] || SCENARIO_LIST_NO_DATA,
  });
  
  export const toModel = (scenarioList = []) => ({
    payload: {
      [SCENARIO_LIST]: scenarioList,
    },
  });
  