import {
    RESET_SCENARIOS,
    UPDATE_SELECTED_SCENARIOS,
  } from '../constants/actionTypes';
  
  export const update = ({ value, meta }) => ({
    type: UPDATE_SELECTED_SCENARIOS,
    payload: value,
    meta,
  });
  
  export const reset = ({ meta } = {}) => ({
    type: RESET_SCENARIOS,
    meta,
  });
  