import { SET_SCENARIO_VERSION } from '../constants/actionTypes';
import {
  SCENARIO_FILE_VERSION,
  SCENARIO_ID,
  DELTA_COMMENT,
} from '../constants/stateConstants';

const setScenarioVersion = scenario => ({
  type: SET_SCENARIO_VERSION,
  payload: {
    [SCENARIO_ID]: scenario.id,
    [SCENARIO_FILE_VERSION]: scenario.fileVersion,
    [DELTA_COMMENT]: scenario.deltaComment,
  },
});

export default setScenarioVersion;
