import configurationDefault from './../config/config.default.json';
import configurationLocal from './../config/config.local.json';
import configurationProd from './../config/config.prod.json';
import configurationSandbox from './../config/config.sandbox.json';
import configurationSit from './../config/config.sit.json';
import configurationUat from './../config/config.uat.json';
import buildConfigurationReducer from '../../services/reducers/buildConfigurationReducer';

const reducer = buildConfigurationReducer({
  default: configurationDefault,
  local: configurationLocal,
  prod: configurationProd,
  sandbox: configurationSandbox,
  sit: configurationSit,
  uat: configurationUat,
});

export default reducer;
