import { REQUEST_BATCHES_FULFILLED } from '../constants/actionTypes';
import { BATCH_LIST, BATCH_LIST_NO_DATA } from '../constants/stateConstants';
import { fromModel } from '../actionAdapters/requestBatchesAdapter';

const reducer = (
  state = {
    [BATCH_LIST]: BATCH_LIST_NO_DATA,
  },
  action
) => {
  switch (action.type) {
    case REQUEST_BATCHES_FULFILLED: {
      const actionModel = fromModel(action);

      return {
        ...state,
        ...actionModel,
      };
    }
    default:
      return state;
  }
};

export default reducer;
