import _ from 'lodash';
import React from 'react';
import { Glyphicon, OverlayTrigger, Tooltip } from 'react-bootstrap';
import PropTypes from 'prop-types';

class GlypIconCell extends React.Component {
  constructor(props) {
    super(props);
    this.openDialog = this.openDialog.bind(this);
  }

  openDialog() {
    this.props.onCellClicked(this.props.cellProps.params.node);
  }

  render() {
    const props = this.props.cellProps;
    const { api, node } = props.params;
    const { onCellClicked, iconName, tooltipText, className } = this.props;
    const tooltip = <Tooltip id="tooltip">{tooltipText}</Tooltip>;

    return (
      <span onClick={this.openDialog}>
        {tooltipText !== '' ? (
          <OverlayTrigger placement="top" overlay={tooltip}>
            <Glyphicon
              glyph={iconName}
              onClick={this.openDialog}
              className={className}
            />
          </OverlayTrigger>
        ) : (
          <Glyphicon glyph={iconName} onClick={this.openDialog} />
        )}
      </span>
    );
  }
}

GlypIconCell.propTypes = {
  iconName: PropTypes.string,
  tooltipText: PropTypes.string,
  onCellClicked: PropTypes.func,
  className: PropTypes.string,
};

GlypIconCell.defaultProps = {
  iconName: undefined,
  tooltipText: undefined,
  onCellClicked: _.noop,
  className: undefined,
};

export default GlypIconCell;
