import { assert, expect } from 'chai';
import toModel from '../../actionAdapters/archiveScenariosAdapter';
import { SCENARIO_IDS } from '../../constants/stateConstants';

describe('archiveScenariosAdapterSpec', () => {
  it('toModel: should generate redux payload', () => {
    const actualScenarioList = ['test'];

    const expectedAction = {
      payload: { [SCENARIO_IDS]: actualScenarioList },
    };

    const state = toModel(actualScenarioList);

    expect(state).to.deep.equal(expectedAction);
  });
});
