import { assert, expect } from 'chai';
import action from '../../actions/archiveScenarioAction';
import { ARCHIVE_SCENARIO } from '../../constants/actionTypes';
import { SCENARIO_IDS } from '../../constants/stateConstants';

describe('archiveScenarioAction', () => {
  it('should generate action', () => {
    const FAKE_SCENARIO = {};

    const actualAction = action([FAKE_SCENARIO]);

    expect(actualAction).to.deep.equal({
      type: ARCHIVE_SCENARIO,
      payload: { [SCENARIO_IDS]: [FAKE_SCENARIO] },
    });
  });
});
