import { assert, expect } from 'chai';
import action from '../../actions/saveBatchAction';
import { SAVE_BATCH } from '../../constants/actionTypes';
import { BATCH } from '../../constants/stateConstants';

describe('saveBatch', () => {
  it('should generate action', () => {
    const FAKE_BATCH = { aBatch: '' };

    const actualAction = action(FAKE_BATCH);

    expect(actualAction).to.deep.equal({
      type: SAVE_BATCH,
      payload: { [BATCH]: FAKE_BATCH },
    });
  });
});
