import { assert, expect } from 'chai';
import action from '../../actions/saveScenarioAction';
import { SAVE_SCENARIO } from '../../constants/actionTypes';
import { SCENARIO } from '../../constants/stateConstants';

describe('saveBatch', () => {
  it('should generate action', () => {
    const FAKE_SCENARIO = { scenerio: '' };

    const actualAction = action(FAKE_SCENARIO);

    expect(actualAction).to.deep.equal({
      type: SAVE_SCENARIO,
      payload: { [SCENARIO]: FAKE_SCENARIO },
    });
  });
});
