import { expect } from 'chai';
import { Observable } from 'rxjs'; // required to pull in RXJS all functions
import configureMockStore from 'redux-mock-store';
import { combineEpics, createEpicMiddleware, EPIC_END } from 'redux-observable';
import { ARCHIVE_SCENARIO } from '../../constants/actionTypes';
import { SCENARIO_IDS } from '../../constants/stateConstants';

import epic, {
  __RewireAPI__ as epicMock,
} from '../../epics/archiveScenariosEpic';

const str = JSON.stringify;

describe('archiveScenariosEpicSpec', () => {
  const FAKE_SCENARIO = { id: 'miles' };
  const FAKE_SCENARIO_LIST = [FAKE_SCENARIO, FAKE_SCENARIO];

  const FAKE_REQUEST_ACTION = { type: 'scenarios' };

  let stream;

  // The api should return an stream which will ultimately trigger a new action
  const mockApi = {
    deleteScenarios: payload => {
      expect(payload.scenarioIds).to.deep.equal(FAKE_SCENARIO_LIST);

      return stream;
    },
  };

  beforeEach(() => {
    stream = Observable.of(FAKE_SCENARIO_LIST);

    epicMock.__Rewire__('batchManagementApiFactory', () => mockApi);
    epicMock.__Rewire__('requestScenariosAction', () => FAKE_REQUEST_ACTION);
  });

  afterEach(() => {
    const mockedCollaborators = [
      'batchManagementApiFactory',
      'requestScenariosAction',
    ];

    mockedCollaborators.forEach(collaborator => epicMock.__ResetDependency__);
  });

  const archiveAction = () => ({
    type: ARCHIVE_SCENARIO,
    payload: { [SCENARIO_IDS]: FAKE_SCENARIO_LIST },
  });

  const createStore = () => {
    const epicMiddleware = createEpicMiddleware(combineEpics(epic));
    const mockStore = configureMockStore([epicMiddleware]);
    return mockStore();
  };

  it('when archive scenarios triggered, request scenarios from the api', () => {
    const action = archiveAction();

    const store = createStore();

    store.dispatch(action);

    expect(str(store.getActions())).to.equal(
      str([action, FAKE_REQUEST_ACTION])
    );
  });
});
