import React from 'react';
import { shallow } from 'enzyme';
import { assert, expect } from 'chai';
import DeltaDialog from '../../../routes/batch/deltaDialog';
import {
  logObject,
  logEnzyme,
  logEnzymeText,
} from './../../../../auth/specs/util/testUtils';

describe('DeltaDialog Component', () => {
  const props = {
    active: true,
    onClose: () => true,
    delta: {
      added: [],
      removed: [],
      combined: [],
    },
  };

  beforeEach(() => {});

  afterEach(() => {});

  it('Should render the dialog', () => {
    const wrapper = shallow(<DeltaDialog {...props} />);

    const element = wrapper.find('.delta-dialog').text();
    expect(element).to.be.eql('<Modal />');
  });

  it('Should render the dialog OK button', () => {
    const wrapper = shallow(<DeltaDialog {...props} />);

    const element = wrapper.find('Button').text();
    expect(element).to.be.eql('<Button />');
  });
});
