import _ from 'lodash';

const makeBaseRoutePath = (routes, currentRoute) => {
  const path = [];

  const matchedRoute = routes.find(route => {
    path.push(
      _.chain(route.path)
        .trimStart('/')
        .trimEnd('/')
        .value()
    );

    return route === currentRoute;
  });

  return matchedRoute ? path.join('/') : currentRoute.path;
};

export default makeBaseRoutePath;
